// import [패키지명] from [경로] 이 형식으로 불러와요.
import React from 'react'; 
// js 파일 뿐 아니라 이미지도 가능가능!
import logo from './logo.svg';
// css? 가능!
import './App.css';

function Child(props) {
  return <div>{props.mothername}}</div>;
}

function Mother () {
  const name = '홍마담';
  return <Child mothername={name}/>;
}

function GrandFather () {
  return <Mother />
}

function App () {
  return <GrandFather />
}

export default App;
