const CREATE_TODO = "CREATE_TODO";
const READ_TODOS = "READ_TODOS";
const UPDATE_TODO = "UPDATE_TODO";
const DELETE_TODO = "DELETE_TODO";

// Ï∂îÍ∞ÄÎêú ÏΩîÎìú üëá - Action CreatorÎ•º ÎßåÎì§Ïñ¥ Ï§çÎãàÎã§.
export const createTodo = (payload) => {
  return {
    type: CREATE_TODO,
    payload,
  };
};

export const readTodos = () => {
  return {
    type: READ_TODOS,
  };
};

export const updateTodo = () => {
  return {
    type: UPDATE_TODO,
  };
};

export const deleteTodo = () => {
  return {
    type: DELETE_TODO,
  };
};

const initialState = {
  todoinitialState: [],
};

// Î¶¨ÎìÄÏÑú
const todo = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case CREATE_TODO:
      return {
        ...state,
        todoinitialState: [...state.todoinitialState, action.payload],
      };
    case READ_TODOS:
      return {};
    case UPDATE_TODO:
      return {};
    case DELETE_TODO:
      return {};

    default:
      return state;
  }
};

// Î™®ÎìàÌååÏùºÏóêÏÑúÎäî Î¶¨ÎìÄÏÑúÎ•º export default ÌïúÎã§.
export default todo;
