import React, {useState} from 'react';
import './App.css';
import Header from "./components/header/Header";
import Layout from "./components/layout/Layout";

//1. 최상단에 My Todo List와 React 글자 섹션이 있다.
//2. label + input 해서 추가하기 버튼을 누르면
//   Working 섹션으로 정보가 추가가된다.
//3. 정보를 삭제하기 버튼을 누르면 삭제가 가능하고
//   완료버튼을 누르면 Done..! 섹션으로 이동한다.
//4. Done..! 섹션으로 이동한 정보는 완료버튼이 취소버튼으로
//  바뀌고, 취소를 누르면 다시 Working 버튼으로 이동한다.

function App () {
  const [inputValue, setInputValue] = useState('')
  const [todoLIst, setTodoList] = useState([])
  const addItem = () => {
    console.log("im herererere!", inputValue)
    setTodoList([...todoList, inputValue])
  }
  return (
      <div>
      <Layout />
        <Header />
      <div className="section2">
          <div className="mydisc">
            <div className="forms">
              <label className="label-form">제목</label> 
              <input value={inputValue} className="add-input1" type="text" onChange={(event)=>console.log(event.target.value)}></input>
              <label className="label-form">내용</label>
              <input value={inputValue} className="add-input1" type="text" onChange={(event)=>console.log(event.target.value)}></input>
              <div>
              <button className="mybtn" type="submit" onClick={addItem}>
                추가하기
              </button>
            </div>
              </div>
        </div>
      </div>

      <div className="section3">
        <div className="list-title">Working.. 🔥</div>
        <div className="todocontainer">
          <div className="Wrapper">
            
            <h2 className="todo-title">제목들어가</h2>
            <div>내용이 들어가는 중입니다
            </div>
            <div className="buttons">
            <button className="button1" type="button">삭제하기</button>
            <button className="button2"type="button">완료</button>
          </div>
          </div>
          <div className="list-title">Done..! 🎉</div>
          <div className="todo-wrapper"></div>
      </div>
        </div>
        
    </div>


  )
}
export default App;
