import { isDocument } from "@testing-library/user-event/dist/utils";

const CREATE_TODO = "CREATE_TODO";
const READ_TODOS = "READ_TODOS";
const UPDATE_TODO = "UPDATE_TODO";
const DELETE_TODO = "DELETE_TODO";

// Ï∂îÍ∞ÄÎêú ÏΩîÎìú üëá - Action CreatorÎ•º ÎßåÎì§Ïñ¥ Ï§çÎãàÎã§.
export const createTodo = (payload) => {
  return {
    type: CREATE_TODO,
    payload,
  };
};
// ÏÉÅÏÑ∏Î≥¥Í∏∞ ÌïÑÏöî
export const readTodos = (payload) => {
  return {
    type: READ_TODOS,
    payload,
  };
};
// ÏôÑÎ£å,Ï∑®ÏÜå Î≤ÑÌäºÍ≥º Ïó∞Í≤∞
export const updateTodo = (payload) => {
  // console.log(payload);
  return {
    type: UPDATE_TODO,
    payload,
  };
};
// ÏÇ≠Ï†úÎ≤ÑÌäºÍ≥º Ïó∞Í≤∞
export const deleteTodo = (payload) => {
  return {
    type: DELETE_TODO,
    payload,
  };
};

const initialState = {
  todoinitialState: [],
};

// Î¶¨ÎìÄÏÑú
const todo = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case CREATE_TODO:
      return {
        ...state,
        todoinitialState: [...state.todoinitialState, action.payload],
      };
    case READ_TODOS:
      return {
        ...state,
        todo: state.todoinitialState.filter((todo) => {
          return todo.id == action.payload;
        }),
        //ÎîîÌÖåÏùºÌéòÏù¥ÏßÄ(ÏÉÅÏÑ∏Î≥¥Í∏∞)Î•º Î∂àÎü¨Ïò§Îäî Í∏∞Îä•Ï∂îÍ∞Ä
      };
    case UPDATE_TODO:
      return {
        ...state,
        todoinitialState: state.todoinitialState.map((todo) => {
          if (todo.id === action.payload) {
            return {
              ...todo,
              isDone: !todo.isDone,
            };
          } else {
            return todo;
          }
        }),
        // ÏßÑÌñâÏ§ëÏùºÎïå ÏôÑÎ£å Î≤ÑÌäº
      };
    case DELETE_TODO:
      return {
        ...state,
        todoinitialState: state.todoinitialState.filter((todo) => {
          return todo.id !== action.payload;
        }),
        // ÏôÑÎ£åÎêòÍ±∞ÎÇò ÏßÑÌñâÏ§ëÏùºÎïå Ï∑®ÏÜåÎ≤ÑÌäº
      };

    default:
      return state;
  }
};

// Î™®ÎìàÌååÏùºÏóêÏÑúÎäî Î¶¨ÎìÄÏÑúÎ•º export default ÌïúÎã§.
export default todo;
