      id: 1,
      username: 'velopert',
      email: 'public.velopert@gmail.com'
    },
    {
      id:2,
      username: 'tester',
      email: 'tester@example.com'
    },
    {
      id: 3,
      username: 'liz'
    }
  ];

  const nextId = useRef(4);
  const onCreate = () => {
      // 나중에 구현 할 배열에 항목 추가하는 로직
      // ...

    nextId.current += 1;
  };
  return (
    <>
      <CreateUser />
      <UserList users={users} />
    </>
  );
}

export default App;
