import React from 'react';
function App() {
  
  const handleClick = () => {
    alert('클릭!');
  };
	// <---- 자바스크립트 영역 ---->

  return (
  /* <---- HTML/JSX 영역  ---->*/
    <div
      style={{
        height: '100vh',
        display: ' flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
   <div>
    이것은 내가 만든 App컴포넌트 입니다
    </div>
   <button type="button" onClick="handleClick">클릭!</button>
    </div>
  );
}

export default App;
