import React from 'react';
import './App.css';

//1. 최상단에 My Todo List와 React 글자 섹션이 있다.
//2. label + input 해서 추가하기 버튼을 누르면
//   Working 섹션으로 정보가 추가가된다.
//3. 정보를 삭제하기 버튼을 누르면 삭제가 가능하고
//   완료버튼을 누르면 Done..! 섹션으로 이동한다.
//4. Done..! 섹션으로 이동한 정보는 완료버튼이 취소버튼으로
//  바뀌고, 취소를 누르면 다시 Working 버튼으로 이동한다.

function App () {
  return (
    // <div className="section1">
    //   <div>My Todo List</div>
    //   <div>React</div>
    // </div>
    <div className="section2">
      <div className="mydisc">
        <label className="label-form">제목</label> 
        <input className="add-input1" type="text" onChange={(event)=>console.log(event.target.value)}></input>
        <label className="label-form">내용</label>
        <input className="add-input1" type="text" onChange={(event)=>console.log(event.target.value)}></input>
      <div>
        <button className="mybtn" type="submit">추가하기</button>
      </div>
      </div>
        <button type="submit">추가하기</button>
    </div>
  )
}
export default App;
